import React from 'react';

// Hero/Search Bar Component
export function HeroSearch({ onSearch }) {
  const [origin, setOrigin] = React.useState('');
  const [destination, setDestination] = React.useState('');
  const [date, setDate] = React.useState('');

  const handleSubmit = e => {
    e.preventDefault();
    onSearch({ origin, destination, date });
  };

  return (
    <section className="bg-gradient-to-r from-blue-500 to-indigo-600 text-white py-16">
      <div className="container mx-auto text-center">
        <h2 className="text-4xl font-bold mb-3">Explore Africa's Skies</h2>
        <p className="mb-8 text-lg">
          Discover the best flight deals across the African continent with our comprehensive search engine.
        </p>
        <form onSubmit={handleSubmit} className="bg-white rounded-lg inline-flex overflow-hidden">
          <input
            type="text"
            placeholder="Departure airport"
            className="p-4 w-48 text-gray-800 placeholder-gray-400 focus:outline-none"
            value={origin}
            onChange={e => setOrigin(e.target.value)}
            required
          />
          <input
            type="text"
            placeholder="Arrival airport"
            className="p-4 w-48 text-gray-800 placeholder-gray-400 focus:outline-none"
            value={destination}
            onChange={e => setDestination(e.target.value)}
            required
          />
          <input
            type="date"
            className="p-4 text-gray-800 focus:outline-none"
            value={date}
            onChange={e => setDate(e.target.value)}
            required
          />
          <button type="submit" className="bg-blue-600 text-white px-6">
            Search Flights
          </button>
        </form>
      </div>
    </section>
  );
}
