// src/components/HeroSearch.js
import React, { useEffect, useState } from 'react';

export function HeroSearch({ onSearch }) {
  const [airports, setAirports] = useState([]);
  const [origin, setOrigin] = useState('');
  const [destination, setDestination] = useState('');
  const [date, setDate] = useState('');

  useEffect(() => {
    fetch('/api/airports')
      .then(r=>r.json())
      .then(setAirports)
      .catch(console.error);
  }, []);

  return (
    <section className="…">
      <form onSubmit={e => {
        e.preventDefault();
        onSearch({ origin, destination, date });
      }} className="…">
        <input
          list="airport-list" name="origin"
          value={origin}
          onChange={e=>setOrigin(e.target.value)}
          placeholder="Departure"
          className="…"
          required
        />
        <input
          list="airport-list" name="destination"
          value={destination}
          onChange={e=>setDestination(e.target.value)}
          placeholder="Arrival"
          className="…"
          required
        />
        <input
          type="date"
          value={date}
          onChange={e=>setDate(e.target.value)}
          className="…"
          required
        />
        <button type="submit" className="…">Search Flights</button>

        <datalist id="airport-list">
          {airports.map(a=> (
            <option key={a.code} value={a.code}>
              {a.name}
            </option>
          ))}
        </datalist>
      </form>
    </section>
  );
}
