// client/src/components/HeroSearch.js
import React, { useEffect, useState } from 'react';
import Select from 'react-select';

export function HeroSearch({ onSearch }) {
  const [airports, setAirports] = useState([]);
  const [origin, setOrigin]       = useState(null);
  const [destination, setDestination] = useState(null);
  const [date, setDate]           = useState('');

  // Fetch airport list once on mount
  useEffect(() => {
    fetch('/api/airports')
      .then(r => r.json())
      .then(rows => {
        // rows = [{ code: 'NBO', name: 'Jomo Kenyatta Intl', city: 'Nairobi' }, …]
        setAirports(
          rows.map(a => ({
            value: a.code,
            label: `${a.code} — ${a.city}: ${a.name}`
          }))
        );
      })
      .catch(console.error);
  }, []);

  const handleSubmit = e => {
    e.preventDefault();
    if (!origin || !destination || !date) return;
    onSearch({
      origin:      origin.value,
      destination: destination.value,
      date
    });
  };

  return (
    <section className="bg-gradient-to-r from-blue-500 to-indigo-600 text-white py-16">
      <div className="container mx-auto px-4 text-center">
        <h2 className="text-4xl font-bold mb-3">Explore Africa's Skies</h2>
        <p className="mb-8 text-lg">
          Discover the best flight deals across the African continent with our comprehensive search engine.
        </p>

        <form onSubmit={handleSubmit} className="bg-white rounded-lg inline-flex overflow-hidden mx-auto">
          <Select
            className="w-48"
            classNamePrefix="react-select"
            placeholder="From"
            options={airports}
            value={origin}
            onChange={setOrigin}
            styles={{ menu: provided => ({ ...provided, zIndex: 9999 }) }}
          />
          <Select
            className="w-48"
            classNamePrefix="react-select"
            placeholder="To"
            options={airports}
            value={destination}
            onChange={setDestination}
            styles={{ menu: provided => ({ ...provided, zIndex: 9999 }) }}
          />
          <input
            type="date"
            className="p-4 text-gray-800 focus:outline-none"
            value={date}
            onChange={e => setDate(e.target.value)}
            required
          />
          <button type="submit" className="bg-blue-600 text-white px-6">
            Search Flights
          </button>
        </form>
      </div>
    </section>
  );
}
